import React, { useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Navbar from './components/Navbar';
import { RecipeList } from './components/RecipeList';
import { RecipeDetails } from './components/RecipeDetails';
import { MealPlanner } from './components/MealPlanner';
import { FilterPanel } from './components/FilterPanel';
import { Profile } from './components/Profile';
import { getRecipeById } from './data/recipes';
import { FavoritesProvider } from './contexts/FavoritesContext';

function App() {
  const [currentPage, setCurrentPage] = useState('recipes');
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [searchToggleKey, setSearchToggleKey] = useState(0);

  const handleRecipeSelect = (recipe) => {
    setSelectedRecipe(recipe);
  };

  const handleBackToRecipes = () => {
    setSelectedRecipe(null);
  };

  const handleNavigation = (page) => {
    setCurrentPage(page);
    setSelectedRecipe(null);
  };

  const handleSearchToggle = () => {
    setSearchToggleKey(prev => prev + 1);
  };

  const renderCurrentPage = () => {
    if (selectedRecipe) {
      return (
        <RecipeDetails 
          recipe={selectedRecipe}
          onBack={() => setSelectedRecipe(null)}
        />
      );
    }

    switch (currentPage) {
      case 'recipes':
        return <RecipeList onRecipeSelect={handleRecipeSelect} onSearchToggle={(callback) => { searchToggleCallbackRef.current = callback; }} />;
      case 'meal-planner':
        return <MealPlanner />;
      case 'favorites':
        return <RecipeList onRecipeSelect={handleRecipeSelect} showFavoritesOnly={true} onSearchToggle={(callback) => { searchToggleCallbackRef.current = callback; }} />;
      case 'suggestions':
        return <RecipeList onRecipeSelect={handleRecipeSelect} showSuggestions={true} onSearchToggle={(callback) => { searchToggleCallbackRef.current = callback; }} />;
      case 'profile':
        return <Profile />;
      default:
        return <RecipeList onRecipeSelect={handleRecipeSelect} onSearchToggle={(callback) => { searchToggleCallbackRef.current = callback; }} />;
    }
  };

  return (
    <FavoritesProvider>
      <div className="min-h-screen bg-secondary-50">
        <Navbar 
          currentPage={currentPage}
          onNavigate={handleNavigation}
          onSearchToggle={handleSearchToggle}
        />

        <AnimatePresence mode="wait">
          <motion.main
            key={selectedRecipe ? `recipe-${selectedRecipe.id}` : currentPage}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.3 }}
          >
            {renderCurrentPage()}
          </motion.main>
        </AnimatePresence>
      </div>
    </FavoritesProvider>
  );
}

export default App;
