import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { HeartIcon, ClockIcon, UsersIcon, StarIcon, BookmarkIcon } from '@heroicons/react/outline';
import { HeartIcon as HeartSolidIcon, BookmarkIcon as BookmarkSolidIcon } from '@heroicons/react/solid';

const RecipeCard = ({ recipe, onClick }) => {
  const [isFavorited, setIsFavorited] = useState(false);
  const [isBookmarked, setIsBookmarked] = useState(false);

  const handleFavorite = (e) => {
    e.stopPropagation();
    setIsFavorited(!isFavorited);
  };

  const handleBookmark = (e) => {
    e.stopPropagation();
    setIsBookmarked(!isBookmarked);
  };

  const getDifficultyColor = (difficulty) => {
    switch (difficulty?.toLowerCase()) {
      case 'easy': return 'bg-success-100 text-success-800 border-success-200';
      case 'medium': return 'bg-warning-100 text-warning-800 border-warning-200';
      case 'hard': return 'bg-error-100 text-error-800 border-error-200';
      default: return 'bg-secondary-100 text-secondary-800 border-secondary-200';
    }
  };

  const getTimeColor = (time) => {
    const minutes = parseInt(time);
    if (minutes <= 15) return 'text-success-600';
    if (minutes <= 30) return 'text-warning-600';
    return 'text-error-600';
  };

  return (
    <motion.div
      className="group card-premium floating-card cursor-pointer overflow-hidden"
      onClick={() => onClick?.(recipe)}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.4 }}
      whileHover={{ y: -8 }}
      layout
    >
      {/* Image Container */}
      <div className="relative overflow-hidden rounded-t-2xl">
        <motion.img
          src={recipe.image || '/api/placeholder/400/250'}
          alt={recipe.title}
          className="w-full h-48 sm:h-56 object-cover transition-transform duration-700 group-hover:scale-110"
          loading="lazy"
        />

        {/* Gradient Overlay */}
        <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />

        {/* Action Buttons */}
        <div className="absolute top-3 right-3 flex space-x-2">
          <motion.button
            onClick={handleFavorite}
            className="p-2 bg-white/90 backdrop-blur-sm rounded-full shadow-medium hover:bg-white transition-all duration-200"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            {isFavorited ? (
              <HeartSolidIcon className="h-4 w-4 text-red-500" />
            ) : (
              <HeartIcon className="h-4 w-4 text-secondary-600" />
            )}
          </motion.button>

          <motion.button
            onClick={handleBookmark}
            className="p-2 bg-white/90 backdrop-blur-sm rounded-full shadow-medium hover:bg-white transition-all duration-200"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            {isBookmarked ? (
              <BookmarkSolidIcon className="h-4 w-4 text-primary-500" />
            ) : (
              <BookmarkIcon className="h-4 w-4 text-secondary-600" />
            )}
          </motion.button>
        </div>

        {/* Rating Badge */}
        {recipe.rating && (
          <div className="absolute top-3 left-3">
            <div className="flex items-center space-x-1 bg-white/90 backdrop-blur-sm px-2 py-1 rounded-full shadow-medium">
              <StarIcon className="h-3 w-3 text-yellow-500 fill-current" />
              <span className="text-xs font-medium text-secondary-800">{recipe.rating}</span>
            </div>
          </div>
        )}

        {/* Quick View Button */}
        <motion.div
          className="absolute bottom-3 left-1/2 transform -translate-x-1/2 opacity-0 group-hover:opacity-100 transition-all duration-300"
          initial={{ y: 10 }}
          whileHover={{ y: 0 }}
        >
          <button className="btn-primary text-sm px-4 py-2">
            Quick View
          </button>
        </motion.div>
      </div>

      {/* Content */}
      <div className="p-5">
        {/* Title */}
        <h3 className="heading-sm text-secondary-900 mb-2 line-clamp-2 group-hover:text-primary-600 transition-colors duration-200">
          {recipe.title}
        </h3>

        {/* Description */}
        {recipe.description && (
          <p className="body-sm text-secondary-600 mb-4 line-clamp-2">
            {recipe.description}
          </p>
        )}

        {/* Tags */}
        {recipe.tags && recipe.tags.length > 0 && (
          <div className="flex flex-wrap gap-1.5 mb-4">
            {recipe.tags.slice(0, 3).map((tag, index) => (
              <span
                key={index}
                className="badge-primary text-xs px-2 py-1"
              >
                {tag}
              </span>
            ))}
            {recipe.tags.length > 3 && (
              <span className="badge-secondary text-xs px-2 py-1">
                +{recipe.tags.length - 3}
              </span>
            )}
          </div>
        )}

        {/* Meta Information */}
        <div className="flex items-center justify-between text-sm">
          <div className="flex items-center space-x-4">
            {/* Cooking Time */}
            {recipe.cookTime && (
              <div className="flex items-center space-x-1">
                <ClockIcon className={`h-4 w-4 ${getTimeColor(recipe.cookTime)}`} />
                <span className="text-secondary-600 font-medium">{recipe.cookTime}m</span>
              </div>
            )}

            {/* Servings */}
            {recipe.servings && (
              <div className="flex items-center space-x-1">
                <UsersIcon className="h-4 w-4 text-secondary-500" />
                <span className="text-secondary-600 font-medium">{recipe.servings}</span>
              </div>
            )}
          </div>

          {/* Difficulty Badge */}
          {recipe.difficulty && (
            <span className={`badge border ${getDifficultyColor(recipe.difficulty)}`}>
              {recipe.difficulty}
            </span>
          )}
        </div>

        {/* Chef/Author */}
        {recipe.chef && (
          <div className="flex items-center space-x-2 mt-4 pt-4 border-t border-secondary-100">
            <div className="w-6 h-6 bg-gradient-to-r from-primary-500 to-accent-500 rounded-full flex items-center justify-center">
              <span className="text-xs font-medium text-white">
                {recipe.chef.charAt(0).toUpperCase()}
              </span>
            </div>
            <span className="text-xs text-secondary-500 font-medium">by {recipe.chef}</span>
          </div>
        )}
      </div>

      {/* Hover Glow Effect */}
      <div className="absolute inset-0 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none">
        <div className="absolute inset-0 rounded-2xl shadow-[0_0_30px_rgba(249,115,22,0.15)]" />
      </div>
    </motion.div>
  );
};

export default RecipeCard;
